import React, {useState} from 'react';

/* Imported components ......................................... */


import TopSM from './components/TopSM.js'
import MMenu1 from './components/MMenu.js'
import MainPage from './MainPage.js'


/*.............................................................. */

const App = () =>  {
	const [Section, setSection] = useState({sec : 1})

	const Selection = () => {

		if(Section.sec === 1 ){
			return(
				<MainPage/>
			)

		}else{
			return (<div></div>)
		}


	}

	return (
		<div className="App">
			<header className="header_area">
				<TopSM/>
				<MMenu1/>
			</header>

			{Selection()}

		</div>
		  )


}

export default App;
