import React, {useState} from 'react';
import {datos} from './components/datos.js'

/* Imported components ......................................... */


import TopSM from './components/TopSM.js'
import MMenu1 from './components/MMenu.js'
import MainPage from './MainPage.js'


/*.............................................................. */

const App = () =>  {
	const [Section, setSection] = useState({sec : 0})



	const ChangePage = (ref) =>{
		console.log(ref, datos)
		if( ref === "Inicio"){
			setSection({sec:0})
		}
		for(let i = 0 ; i < datos.Tienda.length ; i++  ){
			if( ref === datos.Tienda[i]){
				setSection({sec: i + 1})
			}
		}
		if( ref === "Inicio"){
			setSection({sec:0})
		}

	}

	const Selection = () => {

		if(Section.sec === 0 ){
			return(
				<MainPage />
			)

		}else{
			return (<div className="container">Holaa</div>)
		}


	}

	return (
		<div className="App">
			<header className="header_area">
				<TopSM/>
				<MMenu1 OnClick = {ChangePage} State = {Section}/>
			</header>

			{Selection()}

		</div>
		  )


}

export default App;
