import React from 'react'



const PaginationProduct = () =>{

    const reference = [React.createRef(),React.createRef(),React.createRef(),React.createRef(), React.createRef(), React.createRef(), React.createRef()]
    const ref = React.createRef()
    const functions = [()=>{Funcionamiento(reference[0])},()=>{Funcionamiento(reference[1])},()=>{Funcionamiento(reference[2])},()=>{Funcionamiento(reference[3])},()=>{Funcionamiento(reference[4])},()=>{Funcionamiento(reference[5])},()=>{Funcionamiento(reference[6])},]
    const Styles  = ["page-item active","page-item","page-item","page-item blank","page-item"]
    const [pag, setPag] = React.useState({pagination:[1, 2, 3,"...",6]})
    const amountLi = 5
   
    const Funcionamiento = (ref) =>{
        console.log(ref.current)
        var aux = parseInt(ref.current.innerText)
        setPag({pagination:[aux,aux + 1 , aux + 2,"...",aux + 5]})
        console.log(parseInt(ref.current.innerText))
    }
    const List = () =>{
        const txtJXS = []
        for( var i = 0; i < amountLi ; i++){
                txtJXS.push(
                    <li className={Styles[i]} onClick={functions[i]} ref={ref} key={i}>
                        <a className="page-link" ref={reference[i]}>
                            {pag.pagination[i]}
                        </a>
                    </li>)
            
        }
        return [txtJXS]
    }
    
    return(
        <div className="right_page ml-auto">
            <nav className="cat_page" aria-label="Page navigation example">
                <ul className="pagination" >
                    <li className="page-item" onClick={functions[5]}><a className="page-link" ref={reference[5]} id="izquierda"><i className="fa fa-long-arrow-left" aria-hidden="true" ></i></a></li>
                        {List()}
                    <li className="page-item" onClick={functions[6]}><a className="page-link" ref={reference[6]} id="derecha"><i className="fa fa-long-arrow-right" aria-hidden="true" ></i></a></li>
                </ul>
            </nav>
        </div>
    )
}
export default PaginationProduct
