import React, {useRef} from 'react'
import {connect} from 'react-redux'
import {updateWord} from '../../store/palabra/action'

const Form = ({updateWord}) =>{
    const Referencia = useRef(null)
    return(
        <div>
            <input type="" ref={Referencia}></input>
            <button onClick={()=>{updateWord(Referencia.current.value)}}> Guardar en la store  </button>
        </div>
    )
}

export default connect(null, {updateWord})(Form)
