const valores = {
    uno: "1",
    dos: "2",
    tres: "3"
 }

const initialState = {
    Amount : 0
}

export const amountReducer = (state = initialState, action) => {
    switch (action.type) {
        case valores.uno:
            return {...state, Amount : 0}
            break
        case valores.dos:
            return {...state, Amount : 1}
            break
        case valores.tres:
            return {...state, Amount : 2}
            break
        
        default:
            return state
            break
    }
    
}
