import React, { createRef } from 'react'


const OpcionesDd = (props) => {

	const Myref = [React.createRef(),React.createRef(),React.createRef(),React.createRef()]
	const Functions = [() => {this.props.OnClick(this.Myref[0].current.innerHTML)},()=>{this.props.OnClick(this.Myref[1].current.innerHTML)},()=>{this.props.OnClick(this.Myref[2].current.innerHTML)},()=>{this.props.OnClick(this.Myref[3].current.innerHTML)}]
	

	const ManejadorJSX = () => {

		const JSX = [];
		for(let i  = 0 ;  i < this.props.NumItems ; i++ ){
			JSX.push(<li className="nav-item" key={i} ><a onClick={this.Functions[i]} ref= {this.Myref[i]} className="nav-link" href="#" key={i} >{this.props.NameItems[i]}</a></li>)
			
		}
		return [...JSX];
	}

		return(
			<li className="nav-item submenu dropdown" >
				<a href="#" className="nav-link dropdown-toggle" data-toggle="dropdown" role="button" aria-haspopup="true" aria-expanded="false">{this.props.Title}</a>
				<ul className="dropdown-menu">
					{ManejadorJSX()}
				</ul>
			</li>
		)

}

export default OpcionesDd
