import React from 'react'



const PaginationProduct = () =>{

    const reference = [React.createRef(),React.createRef(),React.createRef(),React.createRef(), React.createRef()]
    const Styles  = ["page-item active","page-item","page-item","page-item blank","page-item"]
    const [pag, setPag] = React.useState({pagination:[1, 2, 3,"...",6]})
    const amountLi = 5
   
    const Funcionamiento = (ref) =>{
        console.log(ref)
        setPag({pagination:[0, 1, 2,"...",4]})
    }
    const List = () =>{
        const txtJXS = []
        for( var i = 0; i < amountLi ; i++){
                txtJXS.push(
                    <li className={Styles[i]} onClick={Funcionamiento} ref={reference[i]} key={i}>
                        <a className="page-link" >
                            {pag.pagination[i]}
                        </a>
                    </li>)
            
        }
        return [txtJXS]
    }
    
    return(
        <div className="right_page ml-auto">
            <nav className="cat_page" aria-label="Page navigation example">
                <ul className="pagination" >
                    <li className="page-item" onClick={Funcionamiento}><a className="page-link" ><i className="fa fa-long-arrow-left" aria-hidden="true"></i></a></li>
                        {List()}
                    <li className="page-item" onClick={Funcionamiento}><a className="page-link" ><i className="fa fa-long-arrow-right" aria-hidden="true"></i></a></li>
                </ul>
            </nav>
        </div>
    )
}
export default PaginationProduct
