import React, {createRef} from 'react'
import {connect} from 'react-redux'

const CategoryFilter = ({Filter}) => {

    const ref = [] 
    const txtToPrint = []
    

    const txtRepeater = () =>{
        Filter.forEach( (filter, index ) => { 
            let txtLi = []
            filter.options.forEach((option, index)=>{
            ref.push(createRef())
            var aux = ref.length - 1
            txtLi.push(<li className={filter.styles[index]} key = { index } ref = {ref[aux]} onClick={()=>(console.log(ref[aux].current.innerHTML))}><a>{option}</a></li>)
            })
            txtToPrint.push(
                <div className="widgets_inner" key={index}>
                    <h4>{filter.category}</h4>
                    <ul className="list">
                        {txtLi}
                    </ul>
                </div>
            )

        });
    }
    txtRepeater()

    return( 
        <aside className="left_widgets p_filter_widgets">
            <div className="l_w_title">
                <h3>BUSCAR TAMBIEN POR</h3>
            </div>
            {txtToPrint}
        </aside>
        )
    
}

const mapStateToProps = state =>({
    Filter : state.FilterReducer
})

export default connect(mapStateToProps,{})(CategoryFilter)
