import React, {useState} from 'react'

/* ----------------COMPONENTS------------------------ */

import PtopBar from './PtopBar'
import CategoryProducts,{Repeater} from './CategoryProducts.js'
import Filter from './Filter.js'

/* -------------------------------------------------- */
/* -----------------------DATES---------------------- */

import {datos} from './datos.js' 

/* -------------------------------------------------- */

const ProductArea = (props) =>{
    const nums = [12,15,18]
    let txtJXS = []
    let sw = 1
    const [Pag, setPag] = useState({num: 0})
    const Repeater = () =>{
        
        for(let i = 0 ; i < 3 ; i++){
            let j = 0
            txtJXS[i] = new Array()
                txtJXS[i][j] = new Array()
                datos.CPImages.forEach((datos, index) => {             
                    if (index < nums[i]*(j+1) ){               
                        let aux = index-(nums[i])*(j)
                        txtJXS[i][j][aux] = (
                            <div className="col-lg-4 col-md-4 col-sm-6" key={index}>
                                <div className="f_p_item">
                                    <div className="f_p_img">
                                        <img className="img-fluid" src={datos.url} alt=""/>
                                        <div className="p_icon">
                                            <a href="#"><i className="lnr lnr-heart"></i></a>
                                            <a href="#"><i className="lnr lnr-cart"></i></a>
                                        </div>
                                    </div>
                                    <a href="#"><h4>{datos.name}</h4></a>
                                    <h5>{datos.price}</h5>
                                </div>
                            </div>
                        )
                        
                    }else{
                        j++
                        txtJXS[i][j] = new Array()
                        txtJXS[i][j][0]=(
                            <div className="col-lg-4 col-md-4 col-sm-6" key={index}>
                                <div className="f_p_item">
                                    <div className="f_p_img">
                                        <img className="img-fluid" src={datos.url} alt=""/>
                                        <div className="p_icon">
                                            <a href="#"><i className="lnr lnr-heart"></i></a>
                                            <a href="#"><i className="lnr lnr-cart"></i></a>
                                        </div>
                                    </div>
                                    <a href="#"><h4>{datos.name}</h4></a>
                                    <h5>{datos.price}</h5>
                                </div>
                            </div>
                        )
                    }
                })
                
            
        } 
          
    }
    Repeater()
    const [showItems, setShowItems] = useState({text:txtJXS[0][0]})
    

    const pickUpPag = (props) =>{
        setShowItems({text:txtJXS[show-1][props]})
        /*setPag({num: props})*/
    }

    const showProduct = (show, number) =>{
            setShowItems({text:txtJXS[show-1][0]})   
   
    }
    return(
        <section className = " cat_product_area p_120">
            <div className="container">
                <div className ="row flex-row-reverse">
                    <div className=" col-lg-9 ">
                        <PtopBar OnClick={showProduct} Pag = {pickUpPag}  />
                        <CategoryProducts TextToPrint = {showItems.text} Pag = {Pag} />
                    </div>
                    <div className=" col-lg-3">
                        <Filter/>
                    </div>
                </div>
            </div>
        </section>
    )

}

export default ProductArea
