import React,{useState, useEffect} from 'react'

const TimerDate = (props) => {
    const [state, setState] = useState({Days : 10, Hours : 12, Minutes : 25, Seconds : 24, Value: "1"})
    const Tags = ["Days", "Hours", "Minutes", "Seconds"]
    let {days, hours, minutes, seconds, value} = state
    const Date = props.Date
    
    useEffect((props,state) => {
        const interval = setInterval((state) => {

            if(seconds > 0){
                
                seconds = seconds - 1
            }else{
                seconds = 60
                minutes = minutes -1
                if(minutes < 0 ){
                    minutes = 60
                    hours = hours - 1
                    if(hours<0){
                        hours = 24
                        days = days - 1
                    }
                }
            }
            switch(Date){
                case Tags[0]:
                    value = Tags[0];
                    break;
                case Tags[1]:
                    value = Tags[1];
                    break;
                case Tags[2]:
                    value = Tags[2];
                    break;
                case Tags[3]:
                    value = Tags[3];
                    console.log(value)
                    break;
                }
            setState({ Days : days , Hours : hours, Minutes : minutes, Seconds : seconds, Value: value})
            

        }, 1000);
        return () => clearInterval(interval);
      }, []);

    return(
        <div className={"timer__section "+ props.Date}>
            <div className="timer__number">{ state.Value }</div>
            <div className="timer__label">{props.Date}</div>
        </div>
        
    )
}

export default TimerDate
