import React, { createRef } from 'react'


class OpcionesDd extends React.Component {
	constructor(){
		super();
		this.ManejadorJSX = this.ManejadorJSX.bind(this)
		this.ReactionToClick = this.ReactionToClick.bind(this)
		this.Myref = [React.createRef(),React.createRef(),React.createRef(),React.createRef(),React.createRef(),React.createRef()]
	}

	ReactionToClick(hola){
		console.log(this.Myref[1].current.text, this.Myref[2])

	}

	ManejadorJSX(){

		const JSX = [];
		for(let i  = 0 ;  i < this.props.NumItems ; i++ ){
			JSX.push(<li className="nav-item" key={i} ><a onClick={this.ReactionToClick} ref= {this.Myref[i]} className="nav-link" href="#" key={i} >{this.props.NameItems[i]}</a></li>)
			
		}
		return [...JSX];
	}
	render(){
		return(
			<li className="nav-item submenu dropdown" >
				<a href="#" className="nav-link dropdown-toggle" data-toggle="dropdown" role="button" aria-haspopup="true" aria-expanded="false">{this.props.Title}</a>
				<ul className="dropdown-menu">
					{this.ManejadorJSX(this.props)}
				</ul>
			</li>
		)

	}

}

export default OpcionesDd
