const initialState = {
    pagination: [1, 2, 3,"...",6],
    index: 0
}

export const PAGReducer = (state = initialState, action) =>{
    console.log(state.pagination[0])
    if(action.type === "left"){
        console.log("izquierda" , state.index )
        return{
            ...state,
            index : state.pagination[0] + 1 ,
            pagination: [state.index, state.index+1, state.index + 2, "...", state.index + 5 ]
        }
        
    }else if(action.type === "right"){
        
        var aux = state.pagination[0] - 1
        if(aux!=1){
            return{
                ...state,
                index: state.pagination[0] - 1 ,
                pagination: [state.index, state.index+1, state.index + 2, "...", state.index + 5 ]
            }
        }
        
    }else if(action.type === "NUMBER"){

        return{
            ...state,
            index: parseInt(action.number),
            pagination: [state.index, state.index+1, state.index + 2, "...", state.index + 5 ]
        }
        
    }
    return state
}
