import React, {useState} from 'react';

/* Imported components ......................................... */


import TopSM from './components/TopSM.js'
import MMenu1 from './components/MMenu.js'
import MainPage from './MainPage.js'


/*.............................................................. */

const App = () =>  {
	const [Section, setSection] = useState({sec : 1})

	const Listening = document.getElementById("Categorias")
	Listening.addEventListener("click", ChangePage)

	const ChangePage = () =>{
		if(Section.sec === 1 ){
			setSection({sec:0})
		}

	}

	const Selection = () => {

		if(Section.sec === 1 ){
			return(
				<MainPage />
			)

		}else{
			return (<div className="container">Holaa</div>)
		}


	}

	return (
		<div className="App">
			<header className="header_area">
				<TopSM/>
				<MMenu1 OnClick = {ChangePage} State = {Section}/>
			</header>

			{Selection()}

		</div>
		  )


}

export default App;
