import React, {useState} from 'react'

/* -------------DATES-------------- */
import {datos} from './datos.js' 
/* -------------------------------- */



const CategoryProducts = (props) => {

    const [txtJXS, setTxtJXS] = useState({text : []});
    const Repeater = datos.CPImages.forEach((datos, index)=>{
        if (index < props.AmountToPrint){
            txtJXS.text.push(
                <div className="col-lg-4 col-md-4 col-sm-6" key={index}>
                    <div className="f_p_item">
                        <div className="f_p_img">
                            <img className="img-fluid" src={datos.url} alt=""/>
                            <div className="p_icon">
                                <a href="#"><i className="lnr lnr-heart"></i></a>
                                <a href="#"><i className="lnr lnr-cart"></i></a>
                            </div>
                        </div>
                        <a href="#"><h4>{datos.name}</h4></a>
                        <h5>{datos.price}</h5>
                    </div>
                </div>
            )
        }else{
            
        }
    })
    return(
        <div className="latest_product_inner row">
            {txtJXS}
        </div>
    )
}

export default CategoryProducts
