import React from 'react'

import {Link} from 'react-router-dom'


const OpcionesDd = (props) => {

	
	const Myref = [React.createRef(),React.createRef(),React.createRef(),React.createRef()]
	const Functions = [() => {props.OnClick(Myref[0].current.innerHTML)},()=>{props.OnClick(Myref[1].current.innerHTML)},()=>{props.OnClick(Myref[2].current.innerHTML)},()=>{props.OnClick(Myref[3].current.innerHTML)}]
	

	const ManejadorJSX = () => {

		const JSX = [];
		for(let i  = 0 ;  i < props.NumItems ; i++ ){
			JSX.push(<li className="nav-item" key={i} ><a onClick={Functions[i]} ref= {Myref[i]} className="nav-link" href="#" key={i} >{props.NameItems[i]}</a></li>)
			
		}
		return [...JSX];
	}

		return(
			<li className="nav-item submenu dropdown" >
				<Link to={props.url} className="nav-link dropdown-toggle" data-toggle="dropdown" role="button" aria-haspopup="true" aria-expanded="false">{props.Title}</Link>
				<ul className="dropdown-menu">
					{ManejadorJSX()}
				</ul>
			</li>
		)

}


export default OpcionesDd
