import React, {useState} from 'react'

/* ----------------COMPONENTS------------------------ */

import PtopBar from './PtopBar'
import CategoryProducts,{Repeater} from './CategoryProducts.js'
import Filter from './Filter.js'

/* -------------------------------------------------- */
/* -----------------------DATES---------------------- */

import {datos} from './datos.js' 

/* -------------------------------------------------- */

const ProductArea = (props) =>{
    const [showItems, setShowItems] = useState({text:[],num : 12})
    const nums = [12,15,18]
    let txtJXS = []
    let sw = 1
    const Repeater = () =>{
        for(let i = 0 ; i < 3 ; i++){
            txtJXS[i] = new Array()
            datos.CPImages.forEach((datos, index) => {             
                if (index < nums[i]){
                    txtJXS[i][index] = (
                        <div className="col-lg-4 col-md-4 col-sm-6" key={index}>
                            <div className="f_p_item">
                                <div className="f_p_img">
                                    <img className="img-fluid" src={datos.url} alt=""/>
                                    <div className="p_icon">
                                        <a href="#"><i className="lnr lnr-heart"></i></a>
                                        <a href="#"><i className="lnr lnr-cart"></i></a>
                                    </div>
                                </div>
                                <a href="#"><h4>{datos.name}</h4></a>
                                <h5>{datos.price}</h5>
                            </div>
                        </div>
                    )
                    
                }
            })
        }    
    }
    Repeater()
    showProduct(1)
    const showProduct = (show) =>{
        if(show == 1 &&(sw == 1 || sw == 2)){
            sw = 0
            setShowItems({text:txtJXS[0], num: 12})   
        }
        else if (show == 3 &&  (sw == 1 || sw == 0)){
            sw = 2
            setShowItems({text:txtJXS[2],num: 18})  
        }else {
            sw = 1
            setShowItems({text:txtJXS[1],num: 15})     
        }
    }
    return(
        <section className = " cat_product_area p_120">
            <div className="container">
                <div className ="row flex-row-reverse">
                    <div className=" col-lg-9 ">
                        <PtopBar OnClick={showProduct}/>
                        <CategoryProducts AmountToPrint = {showItems.num} TextToPrint = {showItems.text}/>
                    </div>
                    <div className=" col-lg-3">
                        <Filter/>
                    </div>
                </div>
            </div>
        </section>
    )

}

export default ProductArea
