import React, { createRef } from 'react';
import OpcionesDd from './OpcionesDd.js'
import {datos} from './datos.js'




class MenuDN extends React.Component {
	constructor(){
		super();
		this.Ref = createRef()
		this.function = () => {
			this.props.OnClick(this.Ref.current.innerHTML)
		}
	}
	render(){
		return(
			<div className="collapse navbar-collapse " id="navbarSupportedContent">
				<ul className="nav navbar-nav menu_nav ml-auto">
					<li className="nav-item active"><a className="nav-link" href="#" ref = {this.Ref} onClick={this.function}>datos.MenuDN[0]</a></li> 
					<OpcionesDd Title="Tienda" NameItems={datos.MenuDN[1]} NumItems={datos.MenuDN[1].length} OnClick = {this.props.OnClick}/> 
					<OpcionesDd Title="Blog" NameItems={datos.MenuDN[2]} NumItems={datos.MenuDN[2].length} OnClick = {this.props.OnClick}/> 
					<OpcionesDd Title="Perfil" NameItems={datos.MenuDN[3]} NumItems={datos.MenuDN[3].length} OnClick = {this.props.OnClick}/> 
					<OpcionesDd Title="Contact"  NumItems={0} OnClick = {this.props.OnClick}/> 
				</ul>
				<ul className="nav navbar-nav navbar-right">
					<li className="nav-item"><a href="#" className="cart"><i className="lnr lnr lnr-cart"></i></a></li>
					<li className="nav-item"><a href="#" className="search"><i className="lnr lnr-magnifier"></i></a></li>
				</ul>
			</div>
		)

	}
}

export default MenuDN;
