import React,{useState, useEffect} from 'react'

const [state, setState] = useState({Days : 10, Hours : 12, Minutes : 25, Seconds : 24})
let {days, hours, minutes, seconds} = state
const TimerDate = (props) => {

    useEffect(() => {
        const interval = setInterval(() => {
            if(seconds > 0){
                
                seconds = seconds - 1
            }else{
                seconds = 60
                minutes = minutes -1
                if(minutes < 0 ){
                    minutes = 60
                    hours = hours - 1
                    if(hours<0){
                        hours = 24
                        days = days - 1
                    }
                }
            }
            setState({ Days : days , Hours : hours, Minutes : minutes, Seconds : seconds})
            console.log(state)
        }, 1000);
        return () => clearInterval(interval);
      }, []);
    
    return(
        <div className={"timer__section "+ props.Date}>
            <div className="timer__number"></div>
            <div className="timer__label">{props.Date}</div>
        </div>
        
    )
}

export default TimerDate
