import React,{useState, useEffect} from 'react'

const TimerDate = (props) => {
    const [state, setState] = useState({Days : 10, Hours : 12, Minutes : 25, Seconds : 24})
    const [value, setValue] = useState({date : 0})
    const Tags = ["Days", "Hours", "Minutes", "Seconds"]
    let {days, hours, minutes, seconds} = state

    useEffect(() => {
        const interval = setInterval((state, value) => {
            if(seconds > 0){
                
                seconds = seconds - 1
            }else{
                seconds = 60
                minutes = minutes -1
                if(minutes < 0 ){
                    minutes = 60
                    hours = hours - 1
                    if(hours<0){
                        hours = 24
                        days = days - 1
                    }
                }
            }
            setState({ Days : days , Hours : hours, Minutes : minutes, Seconds : seconds})
            setValue({date : decreaseDate(props.Date,state)})
            

        }, 1000);
        return () => clearInterval(interval);
      }, []);

    const decreaseDate = (props,state) =>{
        
        switch(props){
            case Tags[0]:
                return (state.Days);
                break;
            case Tags[1]:
                return (state.Hours);
                break;
            case Tags[2]:
                return (state.Minutes);
                break;
            case Tags[3]:
                console.log(state.Seconds);
                return (state.Seconds);
                console.log(Tags[0])
                break;
        }
    } 

    return(
        <div className={"timer__section "+ props.Date}>
            <div className="timer__number">{ value.date }</div>
            <div className="timer__label">{props.Date}</div>
        </div>
        
    )
}

export default TimerDate
