import React, {createRef} from 'react'


class LeftDorp extends React.Component{

    constructor(){
        super();
        this.Prueba = []
        this.ref1 = createRef()
        this.Aver = this.Aver.bind(this)
    }
    Aver(){
        console.log(this.ref1)
    }

    
    render(){
        
        this.Prueba.push(  

            <select className="show">
                <option value="675">Show 12</option>
                <option value="2">Show 14</option>
                <option value="4">Show 16</option>
            </select>)
        
        return(
            <div className= "left_dorp" key ="1">
                <select className="sorting">
                    <option value="1">Default sorting</option>
                    <option value="2">Default sorting 01</option>
                    <option value="4">Default sorting 02</option>
                </select>
                <select className="show" ref={this.ref1} onClick={this.Aver} >
                    <option value="1" >Show 12</option>
                    <option value="2">Show 14</option>
                    <option value="4">Show 16</option>
                </select>
            </div>
        )
    }
}
export default LeftDorp
