import React,{useState, useEffect} from 'react'

const TimerDate = (props) => {
    const [state, setState] = useState({Days : 10, Hours : 12, Minutes : 25, Seconds : 24})
    const [value, setValue] = useState(0)
    let {days, hours, minutes, seconds} = state

    useEffect(() => {
        const interval = setInterval((state, value) => {
            if(seconds > 0){
                
                seconds = seconds - 1
            }else{
                seconds = 60
                minutes = minutes -1
                if(minutes < 0 ){
                    minutes = 60
                    hours = hours - 1
                    if(hours<0){
                        hours = 24
                        days = days - 1
                    }
                }
            }
            setState({ Days : days , Hours : hours, Minutes : minutes, Seconds : seconds})
            setValue(decreaseDate(props))
            console.log(seconds)

        }, 1000);
        return () => clearInterval(interval);
      }, []);

    const decreaseDate = (props) =>{
        switch(props.Date, state){
            case "Days":
                return (state.Days);
                break;
            case "Hours":
                return (state.Hours);
                break;
            case "Minutes":
                return (state.Minutes);
                break;
            case "Seconds":
                return (state.Seconds);
                break;
        }
    } 

    return(
        <div className={"timer__section "+ props.Date}>
            <div className="timer__number">{ value }</div>
            <div className="timer__label">{props.Date}</div>
        </div>
        
    )
}

export default TimerDate
