import React from 'react'

/* -------------DATES-------------- */
import {datos} from './datos.js' 
/* -------------------------------- */



class  CategoryProducts extends React.Component{

    constructor(){
        super()
        this.state = {text : [], num: 12}
        this.Repeater = this.Repeater.bind(this)
    }
    Repeater(){
        this.setState({text : [], num: this.props.AmountToPrint})

        datos.forEach((datos, index) => {             
            if (index < this.props.AmountToPrint){
                this.state.text.push(
                    <div className="col-lg-4 col-md-4 col-sm-6" key={index}>
                        <div className="f_p_item">
                            <div className="f_p_img">
                                <img className="img-fluid" src={datos.url} alt=""/>
                                <div className="p_icon">
                                    <a href="#"><i className="lnr lnr-heart"></i></a>
                                    <a href="#"><i className="lnr lnr-cart"></i></a>
                                </div>
                            </div>
                            <a href="#"><h4>{datos.name}</h4></a>
                            <h5>{datos.price}</h5>
                        </div>
                    </div>
                )
                
            }
        })
        return [this.state.text]      
    }
    render() {
        return(
            <div className="latest_product_inner row">
                {(this.props.AmountToPrint != this.state.num) ? this.Repeater() : this.state.text }
            </div>
        )
    }
}

export default CategoryProducts
