import React, {useState} from 'react'

/* ----------------COMPONENTS------------------------ */

import PtopBar from './PtopBar'
import CategoryProducts from './CategoryProducts.js'
import Filter from './Filter.js'

/* -------------------------------------------------- */
/* -----------------------DATES---------------------- */

import {datos} from './datos.js' 

/* -------------------------------------------------- */

const ProductArea = (props) =>{
    const [showItems, setShowItems] = useState({num : 12})
    const [txtJXS, setTxtJXS] = useState({text : []});
    let sw = 0

    const showProduct = (show) =>{
        if (show == 1 && sw != 0){

            setShowItems({num: 12})
            datos.CPImages.pop()
            console.log("this is one")
            sw =0

        }
        if (show == 2 && sw != 1){

            setShowItems({num: 15})
            datos.CPImages.pop()
            console.log("this is two")
            sw = 1
        }
        if (show == 3 && sw !=2){

            setShowItems({num: 18})
            datos.CPImages.pop()
            console.log("this is three")
            sw = 2
        }
    }
    return(
        <section className = " cat_product_area p_120">
            <div className="container">
                <div className ="row flex-row-reverse">
                    <div className=" col-lg-9 ">
                        <PtopBar OnClick={showProduct}/>
                        <CategoryProducts AmountToPrint = {showItems.num} TextToPrint={txtJXS}/>
                    </div>
                    <div className=" col-lg-3">
                        <Filter/>
                    </div>
                </div>
            </div>
        </section>
    )

}

export default ProductArea
