const initialState = {Filter: [
    {category: "Lugar", options: ["EN LA ZONA","EN EL PAIS","CUALQUIER PARTE"], styles: ["","","active"]},
    {category: "Popularidad", options: ["ALTA","MEDIA","BAJA"], styles: ["","","active"]},
    {category: "Precio", options: ["0 - 100 $","101 - 500 $","501 - 1000$","1001 - 5000$","MAYOR A 5000$"], styles: ["","","","","active"]}]}

export const FilterReducer = (state = initialState, action) => {
    if(action.type === "CHANGE_STYLE"){
        let aux1 = 0
        let aux2 = 0
        state.Filter.forEach((filter, i)=>{
            filter.options.forEach((element, index)=>{
                if(action.li === element){
                    aux1 = i
                    aux2 = index
                }
            })
        })
        let ArrayAux = state.Filter[aux1].styles
        let index1 = ArrayAux.findIndex((element)=> 
            element === "active"
        )
        ArrayAux.splice(index1,1,"")
        ArrayAux.splice(aux2,1,"active")
        
        switch (aux1) {
            case 0:
                return {
                    ...state, 
                    Filter : 
                    [
                        {category: state.Filter[0].category, options:state.Filter[0].options, styles: ArrayAux },
                        state.Filter[1],
                        state.Filter[2]
                    ]
                }               
                
                break;
            default:
                return state
                break;
        }
    }

    return state


}
