import React from 'react'
import {connect} from 'react-redux'

import {ChosePage} from '../store/Pagination/action' 


const PaginationProduct = ({ChosePage,datos}) =>{

    const reference = [React.createRef(),React.createRef(),React.createRef(),React.createRef(), React.createRef(), React.createRef(), React.createRef()]
    const ref = React.createRef()
    const functions = [()=>{ChosePage("NUMBER",reference[0].current.innerText)},()=>{ChosePage("NUMBER",reference[1].current.innerText)},()=>{ChosePage("NUMBER",reference[2].current.innerText)},()=>{ChosePage("NUMBER",reference[3].current.innerText)},()=>{ChosePage("NUMBER",reference[4].current.innerText)},()=>{ChosePage(reference[5].current.id)},()=>{ChosePage(reference[6].current.id)},]
    const Styles  = ["page-item active","page-item","page-item","page-item blank","page-item"]
    const [pag, setPag] = React.useState({pagination:[1, 2, 3,"...",6]})
    const amountLi = 5
    
    const List = () =>{
        const txtJXS = []
        for( var i = 0; i < amountLi ; i++){
                txtJXS.push(
                    <li className={Styles[i]} onClick={functions[i]} ref={ref} key={i}>
                        <a className="page-link" ref={reference[i]}>
                            {datos.pagination[i]}
                        </a>
                    </li>)
            
        }
        return [txtJXS]
    }
    
    return(
        <div className="right_page ml-auto" >
            <nav className="cat_page" aria-label="Page navigation example">
                <ul className="pagination" >
                    <li className="page-item" onClick={functions[5]} ref={reference[5]} id="left"><a className="page-link" ><i className="fa fa-long-arrow-left" aria-hidden="true" ></i></a></li>
                        {List()}
                    <li className="page-item" onClick={functions[6]} ref={reference[6]} id="right"><a className="page-link" ><i className="fa fa-long-arrow-right" aria-hidden="true" ></i></a></li>
                </ul>
            </nav>
        </div>
    )
}
const mapStateToProps = state =>({
    datos : state.PAGReducer
})

export default connect(mapStateToProps,{ChosePage})(PaginationProduct)
