import React, {useState} from 'react';

/*---------------------- IMPORTED DATA ------------------------- */

import {datos} from './components/datos.js'

/*-------------------------------------------------------------- */

/*-------------------- Imported components ..................... */


import TopSM from './components/TopSM.js'
import MMenu1 from './components/MMenu.js'
import MainPage from './MainPage.js'
import CategoryPage from './CategoryPage.js'


/*.............................................................. */

const App = () =>  {
	const [Section, setSection] = useState({sec : 0})

	const SECTIONS = [

		<MainPage/>,
		<div>laquevoaaseahorita</div>,
		<div>"Detalles del producto "</div>,
		<div>"Revision "</div> , 
		<div>"Confirmacion "</div> , 
		<div>"Blog "</div>,
		<div>"Informaci√≥n "</div>,
		<div>"Inicio de sesion"</div>,
		<div>"Registrarse "</div>,
		<div>"Contact "</div>

	]


	const ChangePage = (ref) =>{

		let numpage = 0
		for(let i = 0 ; i < datos.MenuDN.length ; i++  ){
			for(let j = 0 ; j < datos.MenuDN[i].length ; j++){
				if( ref === datos.MenuDN[i][j]){
					setSection({sec: numpage})					
				}
				numpage++
			}
		}


	}

	const Selection = () => {

		for(let i = 0 ; i < SECTIONS.length ; i++)
		{
			if(Section.sec === i ){
				return(
					SECTIONS[i]
				)
			}
		}	
	}

	return (
		<div className="App">
			<header className="header_area">
				<TopSM/>
				<MMenu1 OnClick = {ChangePage} State = {Section}/>
			</header>

			{Selection()}

		</div>
		  )


}

export default App;
